definition.script:
pipeline {
    agent any
    stages {
        stage('Stage 1 Test') {
            steps {
                script {
                    if (isUnix()) {
                        sh 'echo Step 1'
                        sh 'exit 0'
                    } else {
                        bat 'echo Step 1'
                        bat 'exit 0'
                    }
                }
            }
        }
        stage('Stage 2 Message') {
            steps {
                script {
                    if (isUnix()) {
                        sh 'echo Step 2'
                        sh 'echo Success!'
                    } else {
                        bat 'echo Step 2'
                        bat 'echo Success!'
                    }
                }
            }
        }
    }
    post {
        always {
            echo 'This will always run'
        }
        success {
            echo 'This will run only if successful'
        }
        failure {
            echo 'This will run only if failed'
        }
        unstable {
            echo 'This will run only if marked as unstable'
        }
        changed {
            echo 'This will run only if changed'
        }
    }
}
